// src/pages/ExamBuilder.js
import React, { useState, useEffect, useRef } from "react";

let nextQuestionId = 1;

function ExamBuilder() {
  const [courseCode, setCourseCode] = useState("");
  const [instructorName, setInstructorName] = useState("");

  const [questions, setQuestions] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [editingQuestion, setEditingQuestion] = useState(null);

  const [questionText, setQuestionText] = useState("");
  const [questionType, setQuestionType] = useState("single-choice");
  const [options, setOptions] = useState(["", "", "", ""]);

  const [autosaveMessage, setAutosaveMessage] = useState("");
  const [showPreview, setShowPreview] = useState(false);

  const dragItem = useRef(null);
  const dragOverItem = useRef(null);

  // Autosave draft every 10 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      if (!courseCode && !instructorName && questions.length === 0) return;
      const time = new Date().toLocaleTimeString();
      setAutosaveMessage(`Draft saved at ${time}`);
    }, 10000);

    return () => clearInterval(interval);
  }, [courseCode, instructorName, questions]);

  const openAddModal = () => {
    setEditingQuestion(null);
    setQuestionText("");
    setQuestionType("single-choice");
    setOptions(["", "", "", ""]);
    setShowModal(true);
  };

  const openEditModal = (q) => {
    setEditingQuestion(q);
    setQuestionText(q.text);
    setQuestionType(q.type);
    setOptions(q.options || ["", "", "", ""]);
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
  };

  const handleOptionChange = (index, value) => {
    const newOpts = [...options];
    newOpts[index] = value;
    setOptions(newOpts);
  };

  const handleSaveQuestion = () => {
    if (!questionText.trim()) return;

    const trimmedOptions =
      questionType === "single-choice" || questionType === "multiple-choice"
        ? options.filter((opt) => opt.trim() !== "")
        : [];

    if (editingQuestion) {
      setQuestions((prev) =>
        prev.map((q) =>
          q.id === editingQuestion.id
            ? { ...q, text: questionText, type: questionType, options: trimmedOptions }
            : q
        )
      );
    } else {
      const newQuestion = {
        id: nextQuestionId++,
        text: questionText,
        type: questionType,
        options: trimmedOptions,
      };
      setQuestions((prev) => [...prev, newQuestion]);
    }

    setShowModal(false);
  };

  const handleDeleteQuestion = (id) => {
    setQuestions((prev) => prev.filter((q) => q.id !== id));
  };

  // Drag & Drop reordering
  const handleDragStart = (index) => {
    dragItem.current = index;
  };

  const handleDragEnter = (index) => {
    dragOverItem.current = index;
  };

  const handleDrop = () => {
    const updatedQuestions = [...questions];
    const draggedItemContent = updatedQuestions[dragItem.current];

    updatedQuestions.splice(dragItem.current, 1);
    updatedQuestions.splice(dragOverItem.current, 0, draggedItemContent);

    dragItem.current = null;
    dragOverItem.current = null;
    setQuestions(updatedQuestions);
  };

  const handleFinalSave = () => {
    if (!courseCode.trim() || !instructorName.trim()) {
      alert("Please fill in Course Code and Instructor Name.");
      return;
    }
    if (questions.length === 0) {
      alert("Add at least one question before saving.");
      return;
    }

    alert("Exam saved successfully! Returning to instructor homepage...");
  };

  return (
    <div>
      <h2 className="mb-4">Exam Creation</h2>

      {/* Exam creation form */}
      <div className="card mb-4">
        <div className="card-body">
          <div className="row g-3">
            <div className="col-md-6">
              <label className="form-label">Course Code</label>
              <input
                type="text"
                className="form-control"
                value={courseCode}
                onChange={(e) => setCourseCode(e.target.value)}
                placeholder="e.g., CS101"
              />
            </div>
            <div className="col-md-6">
              <label className="form-label">Instructor Name</label>
              <input
                type="text"
                className="form-control"
                value={instructorName}
                onChange={(e) => setInstructorName(e.target.value)}
                placeholder="Instructor Name"
              />
            </div>
          </div>
        </div>
      </div>

      {/* Buttons */}
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h4>Exam Questions</h4>
        <div>
          <button className="btn btn-primary me-2" onClick={openAddModal}>
            + Add Question
          </button>
          <button
            className="btn btn-outline-secondary me-2"
            onClick={() => setShowPreview((p) => !p)}
          >
            {showPreview ? "Hide Preview" : "Preview Exam"}
          </button>
          <button className="btn btn-success" onClick={handleFinalSave}>
            Save Exam
          </button>
        </div>
      </div>

      {/* Autosave message */}
      {autosaveMessage && (
        <div className="alert alert-info py-2 mb-3">
          <small>{autosaveMessage}</small>
        </div>
      )}

      {/* Question list */}
      {questions.length === 0 ? (
        <p className="text-muted">No questions added yet. Click "Add Question" to begin.</p>
      ) : (
        <ul className="list-group mb-4">
          {questions.map((q, index) => (
            <li
              key={q.id}
              className="list-group-item d-flex justify-content-between align-items-start"
              draggable
              onDragStart={() => handleDragStart(index)}
              onDragEnter={() => handleDragEnter(index)}
              onDragEnd={handleDrop}
              onDragOver={(e) => e.preventDefault()}
            >
              <div>
                <span className="badge bg-secondary me-2">{index + 1}</span>
                <strong>{q.text}</strong>
                <div>
                  <small className="text-muted">Type: {q.type}</small>
                  {q.options && q.options.length > 0 && (
                    <ul className="mb-0">
                      {q.options.map((opt, i) => (
                        <li key={i}>
                          <small>{opt}</small>
                        </li>
                      ))}
                    </ul>
                  )}
                </div>
              </div>

              <div>
                <button
                  className="btn btn-sm btn-outline-primary me-2"
                  onClick={() => openEditModal(q)}
                >
                  ‚úèÔ∏è Edit
                </button>
                <button
                  className="btn btn-sm btn-outline-danger"
                  onClick={() => handleDeleteQuestion(q.id)}
                >
                  üóë Delete
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}

      {/* Preview Exam */}
      {showPreview && (
        <div className="card mb-4">
          <div className="card-header bg-light">
            <strong>Exam Preview (Read-Only)</strong>
          </div>
          <div className="card-body">
            <h5>
              {courseCode || "Course Code"} ‚Äì Instructor: {instructorName || "Name"}
            </h5>
            <hr />
            <ol>
              {questions.map((q) => (
                <li key={q.id} className="mb-3">
                  {q.text}
                  {(q.type === "single-choice" || q.type === "multiple-choice") &&
                    q.options.length > 0 && (
                      <ul>
                        {q.options.map((opt, i) => (
                          <li key={i}>{opt}</li>
                        ))}
                      </ul>
                    )}
                  {q.type === "short-answer" && <em>Short answer field</em>}
                </li>
              ))}
            </ol>
          </div>
        </div>
      )}

      {/* MODAL */}
      {showModal && (
        <div
          className="modal d-block"
          style={{
            backgroundColor: "rgba(0,0,0,0.5)",
            position: "fixed",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            overflowY: "auto",
            zIndex: 1050
          }}
        >
          <div className="modal-dialog modal-lg mt-5">
            <div className="modal-content">

              <div className="modal-header">
                <h5 className="modal-title">
                  {editingQuestion ? "Edit Question" : "Add Question"}
                </h5>
                <button type="button" className="btn-close" onClick={closeModal}></button>
              </div>

              <div className="modal-body">
                <div className="mb-3">
                  <label className="form-label">Question Text</label>
                  <textarea
                    className="form-control"
                    rows="3"
                    value={questionText}
                    onChange={(e) => setQuestionText(e.target.value)}
                  />
                </div>

                <div className="mb-3">
                  <label className="form-label">Question Type</label>
                  <select
                    className="form-select"
                    value={questionType}
                    onChange={(e) => setQuestionType(e.target.value)}
                  >
                    <option value="single-choice">Single Choice</option>
                    <option value="multiple-choice">Multiple Choice</option>
                    <option value="short-answer">Short Answer</option>
                  </select>
                </div>

                {(questionType === "single-choice" ||
                  questionType === "multiple-choice") && (
                  <div className="mb-3">
                    <label className="form-label">Answer Options</label>
                    {options.map((opt, index) => (
                      <input
                        key={index}
                        type="text"
                        className="form-control mb-2"
                        placeholder={`Option ${index + 1}`}
                        value={opt}
                        onChange={(e) => handleOptionChange(index, e.target.value)}
                      />
                    ))}
                  </div>
                )}
              </div>

              <div className="modal-footer">
                <button className="btn btn-secondary" onClick={closeModal}>
                  Cancel
                </button>
                <button className="btn btn-primary" onClick={handleSaveQuestion}>
                  {editingQuestion ? "Save Changes" : "Add Question"}
                </button>
              </div>

            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default ExamBuilder;
