import axios from "axios";

const API_BASE = "http://localhost:5000";  // your Flask base URL

// ----------------------
// U3-F1: Create Exam
// ----------------------
export async function createExam(data) {
  return axios.post(`${API_BASE}/exams`, {
    course_code: data.courseCode,
    instructor_email: data.instructorEmail,
    title: data.title || "Untitled Exam",
    time_limit: data.timeLimit || 60,
    security_settings: "shuffle=true"
  });
}

// ----------------------
// U3-F2: Add Question
// ----------------------
export async function addQuestion(examId, question) {
  return axios.post(`${API_BASE}/exams/${examId}/questions`, {
    question_text: question.text,
    is_multiple_correct: question.type === "multiple",
    points: question.points || 1,
    options: question.options.map(opt => ({
      option_text: opt.text,
      is_correct: opt.isCorrect
    }))
  });
}

// ----------------------
// U3-F3: Edit Question
// ----------------------
export async function updateQuestion(questionId, question) {
  return axios.patch(`${API_BASE}/exams/questions/${questionId}`, {
    question_text: question.text,
    is_multiple_correct: question.type === "multiple",
    points: question.points || 1,
    options: question.options.map(opt => ({
      option_text: opt.text,
      is_correct: opt.isCorrect
    }))
  });
}

// ----------------------
// U3-F4: Delete Question
// ----------------------
export async function deleteQuestion(questionId) {
  return axios.delete(`${API_BASE}/exams/questions/${questionId}`);
}

// ----------------------
// U3-F5: Reorder Questions
// ----------------------
export async function reorderQuestions(examId, orderedQuestions) {
  return axios.post(`${API_BASE}/exams/${examId}/reorder`, {
    order: orderedQuestions.map((q, i) => ({
      question_id: q.id,
      order_index: i + 1
    }))
  });
}

// ----------------------
// U3-F6: Update Exam Options (time limit, security)
// ----------------------
export async function updateExamOptions(examId, options) {
  return axios.patch(`${API_BASE}/exams/${examId}/options`, options);
}

// ----------------------
// U3-F7: List exams by instructor
// ----------------------
export async function getExamsByInstructor(email) {
  return axios.get(`${API_BASE}/exams/instructor/${email}`);
}
